function MenuManager(e){this.menuElement=$("[name='"+e+"']")[0];this.subMenus=[this.menuElement];self=this;this.keyHandler=function(e){e.preventDefault();var t=e.keyCode;if(t==27){if(self.subMenus.length!=1){element=self.subMenus.pop();element.classList.remove("open")}}else if(t>=65&&t<=90){t=String.fromCharCode(e.which).toLowerCase();elements=$("[name^='"+t+"']");element=null;for(var n=0;n<=elements.length;n++){if(elements[n].attributes["level"].value==self.subMenus.length){if(elements[n].attributes["parent"].value==self.subMenus[self.subMenus.length-1].attributes["id"].value){element=elements[n];break}}}if(null!=element){tagName=element.tagName.toLowerCase();if(tagName=="li"){element.classList.add("open");self.subMenus.push(element)}else if(tagName=="a"){window.location=element.href}}}}}
